    for i in range(1181, 2000):
        url = f'http://epaper.sxkjbs.com/epaper/periodid-{i}.shtml'
        response = requests.get(url, headers=headers)
        if response.status_code == 200:
            content = response.content.decode()
            html = etree.HTML(content)
            date_str = ''.join(html.xpath("//div[@class='date']/text()"))
            # 2024年09月02日
            date_time = re.findall(r'\d{4}年\d{2}月\d{2}日', date_str)[0]
            date = datetime.strptime(date_time, '%Y年%m月%d日').strftime('%Y-%m-%d')
            if date_time not in date_dict:
                date_dict[date] = url
    return date_dict



